const AutoEmailSender = require('./index');

// SMTP configuration
const smtpConfig = {
    host: 'smtp.example.com', // Replace with your SMTP server
    port: 587,
    secure: false, // true for 465, false for other ports
    auth: {
        user: 'your-email@example.com', // Replace with your email
        pass: 'your-email-password', // Replace with your email password
    },
};

// Initialize with the path to your Excel file and SMTP config
const emailSender = new AutoEmailSender('emails.xlsx', smtpConfig);

// Send email for a specific row (0-based index)
const rowIndex = 1;
const subject = "Subject of Your Email";
const templatePath = 'email_template.html';

emailSender.sendEmailForRow(rowIndex, subject, templatePath);
